using Palmmedia.ReportGenerator.Core.CodeAnalysis;
using System;
using System.Collections;
using System.Collections.Generic;
using System.IO;
using System.Net;
using System.Threading;
using UnityEngine;

public class ComputerVisionServer : MonoBehaviour
{
    private HttpListener httpListener;
    private Thread listenerThread;
    private bool isRunning = false;
    private bool isCvServerRunning = false;
    private byte[][] jointsArray;

    private const int maxAttempts = 10;


    private const string shutdownEndpoint = "/shutdown/";
    private const string unityServerURL = "http://localhost:7000";


    private const float cvTreshold = 1.0f;
    private const string cvModel = "model";
    private const string cvProcessEndpoint = "/process";
    private const string cvSettingsEndpoint = "/settings";
    private const string cvShutdownEndpoint = "/shutdown";
    private const string computerVisionServerURL = "http://localhost:8000";
    
    private const string cat_image = "";
    
    private void Start()
    {
        httpListenerSetup();
        isRunning = true;
        listenerThread = new Thread(StartListener);
        listenerThread.Start();
    }

    private void httpListenerSetup()
    {
        httpListener = new HttpListener();
        httpListener.Prefixes.Add(unityServerURL + shutdownEndpoint);
        httpListener.Start();
    }

    private void StartListener()
    {
        while (isRunning)
        {
            try
            {
                HttpListenerContext context = httpListener.GetContext();
                ProcessRequest(context);
            }
            catch (HttpListenerException)
            {
                Debug.LogError("HttpListener has encountered an exception");
                break;
            }
            catch (Exception error)
            {
                Debug.LogError("Server error HTTP: " + error.Message);
            }
        }
    }

    private void StopListener()
    {
        isRunning = false;
        if (httpListener != null && httpListener.IsListening)
        {
            httpListener.Stop();
        }
        if (listenerThread != null && listenerThread.IsAlive)
        {
            listenerThread.Join();
            Debug.Log("Server was stopped correctly");
        }
    }
    private void SendShutdownSignal()
    {
        var shutdownRequest = (HttpWebRequest)WebRequest.Create(computerVisionServerURL + cvShutdownEndpoint);
        shutdownRequest.Method = "POST";
        try
        {
            using (var response = (HttpWebResponse)shutdownRequest.GetResponse())
            {
                if (response.StatusCode == HttpStatusCode.OK)
                {
                    Debug.Log("Computer vision server notified of Unity Server shutdown.");
                }
            }
        }
        catch (Exception error)
        {
            //Debug.LogError("Error notifying computer vision server about shutdown: " + error.Message);
        }
    }

    private void OnApplicationQuit()
    {
        SendShutdownSignal();
        StopListener();
    }



    private void ProcessRequest(HttpListenerContext context)
    {
        HttpListenerRequest request = context.Request;
        HttpListenerResponse response = context.Response;
        if (request.Url.AbsolutePath == shutdownEndpoint)
        {
            ClientShutdownSignal(response);
        }
        response.Close();
    }

    private void ClientShutdownSignal(HttpListenerResponse response)
    {
        Debug.Log("Shutdown signal from computer vision server");
        StopListener();
        response.StatusCode = (int)HttpStatusCode.OK;
        response.OutputStream.Close();
        Debug.Log("Server is shutting down...");
    }

    private void SendImageFrame(string imageBase64)
    {
        var request = (HttpWebRequest)WebRequest.Create(computerVisionServerURL + cvProcessEndpoint);
        request.Method = "POST";
        request.ContentType = "application/json";
        string jsonPayload = "{\"image_base64\": \"}" + imageBase64 + "\"}";
        try
        {
            using (var streamWriter = new StreamWriter(request.GetRequestStream()))
            {
                streamWriter.Write(jsonPayload);
            }
            using (var response = (HttpWebResponse)request.GetResponse())
            {
                using (var streamReader = new StreamReader(response.GetResponseStream()))
                {
                    string result = streamReader.ReadToEnd();
                    Debug.Log(result);
                }
                if (response.StatusCode == HttpStatusCode.OK)
                {
                    Debug.Log("Got the array with points");
                }
            }
        }
        catch (Exception error)
        {
            Debug.LogError("Error from computer vision server: " + error.Message);
        }
    }

    private void SendCVSettings(float threshold, string model)
    {
        var request = (HttpWebRequest)WebRequest.Create(computerVisionServerURL + cvSettingsEndpoint);
        request.Method = "POST";
        request.ContentType = "application/json";
        string jsonPayload = "{\"threshold\": " + threshold + ", \"model\": \"" + model + "\"}";
        try
        {
            using (var streamWriter = new StreamWriter(request.GetRequestStream()))
            {
                streamWriter.Write(jsonPayload);
            }
            using (var response = (HttpWebResponse)request.GetResponse())
            {
                using (var streamReader = new StreamReader(response.GetResponseStream()))
                {
                    string result = streamReader.ReadToEnd();
                    Debug.Log(result);
                }
                if (response.StatusCode == HttpStatusCode.OK)
                {
                    Debug.Log("Settings were changed");
                }
            }
        }
        catch (Exception error)
        {
            Debug.LogError("Error from computer vision server: " + error.Message);
        }
    }




}
